CREATE TABLE components_characteristics
(
    component_name	VARCHAR(512),
    reliability	VARCHAR(512)
);


alter table components_characteristics rename column component_name to group_name;
alter table components_characteristics rename column reliability to reliability_str;
alter table components_characteristics add column reliability DOUBLE PRECISION;

update components_characteristics
set reliability = cast(reliability_str as double precision)
where 1 = 1;

alter table components_characteristics drop column reliability_str;

select *
from components_characteristics
limit 10;